import React, { useState } from "react";
import './App.css';

function App() {
  const [squares, setSquares] = useState([]);

  const addSquare = () => {
    fetch("https://localhost:7044/api/square/randomcolor")
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const newSquare = {
          color: data.color,
        };
        setSquares([...squares, newSquare]);
      })
      .catch(error => {
        console.error("There was an error fetching the color!", error);
      });
  };

  return (
    <div className="App">  
      <div className="square-container">
        {squares.map((square, index) => (
          <div key={index} className="square" style={{ backgroundColor: square.color }}
          ></div>
        ))}
      </div>
      <button onClick={addSquare}>LÃ¤gg till en ruta</button>
    </div>
  );
}

export default App;
