import React, { useState, useEffect } from "react";
import './App.css';

function App() {
  const [squares, setSquares] = useState([]);
  const size = 30; // size of each square

  useEffect(() => {
    // Load saved squares when the component mounts
    fetch("https://localhost:7044/api/square/squares")
      .then(response => response.json())
      .then(data => {
        setSquares(data);
      })
      .catch(error => {
        console.error("Error loading squares:", error);
      });
  }, []);

  const addSquare = () => {
    fetch("https://localhost:7044/api/square/randomcolor")
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const newSquare = {
          color: data.color,
          position: squares.length // Store the position as the index in the squares array
        };
        const updatedSquares = [...squares, newSquare];
        setSquares(updatedSquares);
        saveSquares(updatedSquares); // Save the updated squares to the API
      })
      .catch(error => {
        console.error("There was an error fetching the color!", error);
      });
  };

  const saveSquares = (squaresToSave) => {
    fetch("https://localhost:7044/api/square/squares", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(squaresToSave),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Error saving squares');
      }
    })
    .catch(error => {
      console.error("There was an error saving the squares!", error);
    });
  };

  const clearSquares = () => {
    // Clear the squares in the state
    setSquares([]);

    // Call the API to clear the squares
    fetch("https://localhost:7044/api/square/squares/clear", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify([]), // Send an empty array to clear the squares
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Error clearing squares');
      }
    })
    .catch(error => {
      console.error("There was an error clearing the squares!", error);
    });
  };

  // Dynamically calculate position for alternating row/column pattern
  const calculatePosition = (index) => {
    const group = Math.floor(index / 4); // Every 4 squares (2 rows, 2 columns) forms a group
    const positionInGroup = index % 4; // Determine position within the group

    // Adjust positioning logic based on index within the group
    if (positionInGroup === 0 || positionInGroup === 1) {
      // First two items: Add to rows
      return { row: group * 2 + 1, col: positionInGroup + 1 };
    } else if (positionInGroup === 2) {
      // Third item: Add to new row
      return { row: group * 2 + 2, col: 1 };
    } else {
      // Fourth item: Add to new column
      return { row: group * 2 + 2, col: 2 };
    }
  };

  return (
    <div className="App">
      <div className="square-container">
        {squares.map((square, index) => {
          const { row, col } = calculatePosition(index);
          return (
            <div
              key={index}
              className="square"
              style={{
                backgroundColor: square.color,
                width: `${size}px`,
                height: `${size}px`,
                gridColumn: col,
                gridRow: row,
              }}
            ></div>
          );
        })}
      </div>
      <div className="buttons">
        <button className="add-btn" onClick={addSquare}>LÃ¤gg till en ruta</button>
        <button className="clear-btn" onClick={clearSquares}>Rensa alla rutor</button>
      </div>
    </div>
  );
}

export default App;
