import React, { useState } from "react";
import './App.css';

function App() {
  const [squares, setSquares] = useState([]);
  const size = 30; // Size of each square

  const addSquare = () => {
    fetch("https://localhost:7044/api/square/randomcolor")
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const newSquare = {
          color: data.color,
        };
        setSquares([...squares, newSquare]);
      })
      .catch(error => {
        console.error("There was an error fetching the color!", error);
      });
  };

  // Dynamic calculation for rows and columns to ensure a square-like grid
  const numColumns = Math.ceil(Math.sqrt(squares.length));
  const numRows = Math.ceil(squares.length / numColumns); // Keep it as square-like as possible

  return (
    <div className="App">
      <div
        className="square-container"
        style={{
          display: 'grid',
          gridTemplateColumns: `repeat(${numColumns}, ${size}px)`,  // Dynamic columns
          gridTemplateRows: `repeat(${numRows}, ${size}px)`, // Dynamic rows
          justifyContent: 'center',  // Center the grid horizontally
        }}
      >
        {squares.map((square, index) => (
          <div
            key={index}
            className="square"
            style={{
              backgroundColor: square.color,
              width: `${size}px`,
              height: `${size}px`,
              transition: 'all 0.3s ease', // Smooth transition for each square
            }}
          ></div>
        ))}
      </div>
      <button className="add-btn" onClick={addSquare}>Add Square</button>
      <button className="clear-btn" onClick={() => setSquares([])}>Clear Squares</button>
    </div>
  );
}

export default App;
