import React, { useState } from "react";
import './App.css';

function App() {
  const [squares, setSquares] = useState([]);

  const addSquare = () => {
    fetch("https://localhost:7044/api/square/randomcolor")
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const newSquare = {
          color: data.color,
        };
        setSquares(prevSquares => [...prevSquares, newSquare]);
      })
      .catch(error => {
        console.error("There was an error fetching the color!", error);
      });
  };

  const size = 30; // Size of each square set to 30px
  const numSquares = squares.length; // Total number of squares
  const numColumns = Math.ceil(Math.sqrt(numSquares)); // Number of columns based on the square root of total squares

  return (
    <div className="App">
      <div className="square-container" style={{ gridTemplateColumns: `repeat(${numColumns}, ${size}px)` }}>
        {squares.map((square, index) => (
          <div
            key={index}
            className="square"
            style={{ backgroundColor: square.color }}
          ></div>
        ))}
      </div>
      <button class="add-square-btn" onClick={addSquare}>LÃ¤gg till en ruta</button>
    </div>
  );
}

export default App;
