import React, { useState, useEffect } from "react";
import './App.css';

function App() {
  const [squares, setSquares] = useState([]);
  const size = 30; // size of each square

  useEffect(() => {
    // Load saved squares when the component mounts
    fetch("https://localhost:7044/api/square/squares")
      .then(response => response.json())
      .then(data => {
        setSquares(data);
      })
      .catch(error => {
        console.error("Error loading squares:", error);
      });
  }, []);

  const addSquare = () => {
    fetch("https://localhost:7044/api/square/randomcolor")
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const newSquare = {
          color: data.color,
          position: squares.length
        };
        const updatedSquares = [...squares, newSquare];
        setSquares(updatedSquares);
        saveSquares(updatedSquares); // Save the updated squares to the API
      })
      .catch(error => {
        console.error("There was an error fetching the color!", error);
      });
  };

  const saveSquares = (squaresToSave) => {
    fetch("https://localhost:7044/api/square/squares", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(squaresToSave),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Error saving squares');
      }
    })
    .catch(error => {
      console.error("There was an error saving the squares!", error);
    });
  };

  const clearSquares = () => {
    setSquares([]);
    fetch("https://localhost:7044/api/square/squares/clear", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify([]),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Error clearing squares');
      }
    })
    .catch(error => {
      console.error("There was an error clearing the squares!", error);
    });
  };

  // Calculate the position of each square based on index
  const calculatePosition = (index) => {
    const rowColSwitch = 3; // Number of squares before switching to a new row/column layout
    const isRowMode = Math.floor(index / rowColSwitch) % 2 === 0;

    const basePosition = index % rowColSwitch;

    if (isRowMode) {
      return { row: 1, col: basePosition + 1 }; // Fill row first
    } else {
      return { row: basePosition + 1, col: 1 }; // Then start filling a new column
    }
  };

  return (
    <div className="App">
      <div className="square-container" style={{ gridTemplateColumns: `repeat(auto-fill, ${size}px)` }}>
        {squares.map((square, index) => {
          const { row, col } = calculatePosition(index);
          return (
            <div
              key={index}
              className="square"
              style={{
                backgroundColor: square.color,
                width: `${size}px`,
                height: `${size}px`,
                gridColumn: col,
                gridRow: row,
              }}
            ></div>
          );
        })}
      </div>
      <div className="buttons">
        <button className="add-btn" onClick={addSquare}>Add Square</button>
        <button className="clear-btn" onClick={clearSquares}>Clear All</button>
      </div>
    </div>
  );
}

export default App;
