import React, { useState } from "react";
import './App.css';

function App() {
  const [squares, setSquares] = useState([]);

  const addSquare = () => {
    fetch("https://localhost:7044/api/square/randomcolor")
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const newSquare = {
          color: data.color,
        };
        setSquares(prevSquares => [...prevSquares, newSquare]);
      })
      .catch(error => {
        console.error("There was an error fetching the color!", error);
      });
  };

  // Calculate position based on index
  const calculatePosition = (index) => {
    const size = 30; // Size of the squares
    const padding = 2; // Padding between squares
    const row = Math.floor(Math.sqrt(index)); // Determine row based on index
    const col = index - row * row; // Determine column based on the position in the row

    // Calculate x and y position
    return {
      top: row * (size + padding), // Vertical position
      left: col * (size + padding), // Horizontal position
    };
  };

  return (
    <div className="App">
      <div className="square-container">
        {squares.map((square, index) => {
          const position = calculatePosition(index);
          return (
            <div
              key={index}
              className="square"
              style={{
                backgroundColor: square.color,
                top: position.top,
                left: position.left,
              }}
            ></div>
          );
        })}
      </div>
      <button onClick={addSquare}>LÃ¤gg till en ruta</button>
    </div>
  );
}

export default App;
