import React, { useState } from "react";
import './App.css';

function App() {
  const [squares, setSquares] = useState([]);

  const addSquare = () => {
    fetch("https://localhost:7044/api/square/randomcolor")
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const newSquare = {
          color: data.color,
        };
        setSquares([...squares, newSquare]);
      })
      .catch(error => {
        console.error("There was an error fetching the color!", error);
      });
  };

  // Calculate the size of the squares
  const squareSize = 100; // Each square will be 100px
  const containerSize = Math.ceil(Math.sqrt(squares.length)) * squareSize; // Calculate container size

  return (
    <div className="App">
      <div className="square-container" style={{ width: containerSize, height: containerSize }}>
        {squares.map((square, index) => (
          <div
            key={index}
            className="square"
            style={{
              backgroundColor: square.color,
              left: (index % Math.ceil(Math.sqrt(squares.length))) * squareSize, // Calculate x position
              top: Math.floor(index / Math.ceil(Math.sqrt(squares.length))) * squareSize // Calculate y position
            }}
          ></div>
        ))}
      </div>
      <button onClick={addSquare}>LÃ¤gg till en ruta</button>
    </div>
  );
}

export default App;
